void DQMJobInterfaceWidget::updateJobStatus()
{
    m_pJobInterface->status();

    struct timespec timesleep;
    timesleep.tv_sec = 0;
    timesleep.tv_nsec = 500000000L;
    nanosleep(&timesleep, NULL);

    this->updateStatus(m_pJobInterface->getProcessStatusValue());
}

